var redis=require('redis');

exports.createRedisClient = function(opts) {
	return new RedisClient(opts);
}

function RedisClient(opts) {
	opts = opts || {};
	var config = {
		host: opts.host || '127.0.0.1',
		port: opts.port || 6379,
		database: opts.db || 0
	};
	var client=redis.createClient(config.port,config.host);
	client.select(config.database,function(err,res){
		if(err) console.log('err: ' + err.toString());
	});
	client.on('error', function(err) {
		if(err) console.log('err: ' + err.toString());
	});
	return client;
}